#!/usr/bin/env bash
set -eu
jq --arg name_prefix "$NAME_PREFIX-" --raw-output 'to_entries[] | select(.key|contains(":")) | {key: .key|split(":")|last|sub("\\${platform}";"linux-amd64"), rev:.value.rev, path: .key|split(":")|first} | {key, url: "https://chrome-infra-packages.appspot.com/dl/\(.key)/+/\(.rev)", name:.key|gsub("[^\\da-zA-Z+-._?=]";"-"), path, rev} | "DEP_PATH=\(.path) NAME=\($name_prefix + .name) URL=\(.url) REV=\(.rev) SHA256=`nix-prefetch fetchurl --url $URL --name $NAME` && echo \"fetchurl $DEP_PATH $NAME $URL $REV $SHA256\""' "$REV_INFO_FILE"
jq --arg name_prefix "$NAME_PREFIX-" --raw-output 'to_entries[] | select((.key|contains(":")|not) and .value.rev) | {key, url:.value.url, name:.key|gsub("[^\\da-zA-Z+-._?=]";"-"), rev: .value.rev} | "DEP_PATH=\(.key) URL=\(.url) NAME=\($name_prefix + .name) REV=\(.rev) SHA256=`nix-prefetch fetchgit --url $URL --name $NAME --rev $REV` && echo \"fetchgit $DEP_PATH $NAME $URL $REV $SHA256\""' "$REV_INFO_FILE"
